import { createSlice } from "@reduxjs/toolkit";
import { cartItemsActions } from "./cart";

const initialUIState = {showCart : false};
const ui = createSlice({
    name : "ui",
    initialState : initialUIState,
    reducers : {
        visibleCart(state){state.showCart = true},
        inVisibleCart(state){state.showCart = false}
    }
})

export const getMealCart = () => {
    return async(dispatch) => {
        const getData = async () => {
            const response = await fetch('https://food-app-74f07-default-rtdb.firebaseio.com/cart.json');
            if(!response.ok){
                throw new Error('could not get cart items');
                
            }

            const data = await response.json();
            return data;
        }

        try {
            const data = await getData();
            dispatch(cartItemsActions.replace({
                items : data.items, totalAmount: data.totalAmount, totalQuantity: data.totalQuantity
            }));

        } catch (error) {
            console.log(error);
        }
    }
}



export const putMealCart = (props) => {
    
    return async(dispatch) => {
        
        const putData = async() => {
            const response = await fetch('https://food-app-74f07-default-rtdb.firebaseio.com/cart.json',{
                method : "PUT",
                body : JSON.stringify(cart
                    // !props ? {items: props.items, totalAmount: props.totalAmount, totalQuantity: props.totalQuantity} : {}
                )
            })

            if(!response.ok){
                throw new Error('could not upload to the cart')
            }
        }

        try {
            await putData();
        } catch (error) {
            console.log(error.message)
        }

    }
}

export const uiActions = ui.actions;

export default ui;
