import { configureStore, createSlice } from '@reduxjs/toolkit'; 

const  initialCartState ={Items: [], totalAmount : 0 };

const cartItems = createSlice({
    name: "cartItems",
    initialState: initialCartState,
    reducers:{

        addItemsToCart(state, action){
            const temp = action.payload;
            const item = state.Items.filter( item => item.id===temp.id);
            if(item==={}){
                temp.quantity = 0;
                temp.totalAmount = 0;
                item=temp;
            }
            item.quantity++;
            item.totalAmount += item.amount
            state.totalAmount += item.amount;
        },

        removeItemsToCart(state, action){
            const item = state.Items.filter( item => item.id===action.id );
        }
    }
})

export const artItemsAction = cartItems.actions;

const store = configureStore({
    reducer: { meal: cartItems.reducer}
})

export default store;
