import { cartItemsActions, ui } from "./cart";


export const getItems = () => {
    return async (dispatch) => {
        const getData = async () => {
            const response = await fetch('https://food-app-74f07-default-rtdb.firebaseio.com/cart.json');
            if(!response.ok) {
                throw new Error('could not be able to get cart items');
            }

            const data = await response.json();

            return data; 
        }

        try {
            let data = await getData();
            if(data === null){
                data={
                    items :  [],
                    totalAmount : 0,
                    totalQuantity : 0
                }
            }
            dispatch(cartItemsActions.replaceCart({
                items : data.items || [],
                totalAmount : data.totalAmount,
                totalQuantity : data.totalQuantity
            }))
        } catch (error) {
            console.log("getItems: error", error.message);
        }
    }
}

export const putItems = (cart) => {
    return  async (dispatch) => {
        const putData = async () => {
            const response = await fetch('https://food-app-74f07-default-rtdb.firebaseio.com/cart.json', {
                method : 'PUT',
                body : JSON.stringify({
                    items : cart.Items,
                    totalAmount : cart.totalAmount,
                    totalQuantity : cart.totalQuantity,
                })
            })

            if(!response.ok){
                throw new Error('could not able to upload items on cart');
            }

            console.log("putItems: response", response)
        }

        try {
            putData();
        } catch (error) {
            console.log("putItems: error", error.message)
        }
    }
}
