import classes from './Login.module.css';
import {Link} from 'react-router-dom'
import { useRef, useState } from 'react';

const passwordValidation = (str) => {
    if(str.length>8) return true;
    return false;
}


const Signin = () => {
    const usernameRef = useRef();
    const passwordRef = useRef();
    const emailRef = useRef();
    const [form, setForm] = useState({email : true, password : true});
    
    const signinHandler = (event) => {
        event.preventDefault();
        const username = usernameRef.current.value;
        const password = passwordRef.current.value;
        const email = emailRef.current.value;

        const isValidPassword = passwordValidation(password);
        const isValidEmail = email.include('@', 0);

        const formValid = isValidEmail&&isValidPassword;

        if(!formValid) return;

        setForm({
            email : isValidEmail,
            password : isValidPassword
        })
    }

    return (
        <div className={classes.loginPage}>
            <div className={classes.form}>
                <h3>Sign Up</h3>
                <form className={classes.registerForm} onSubmit={signinHandler}>
                    <input type="text" ref={usernameRef} placeholder="username"/>
                    {}
                    <input type="text" ref={emailRef} placeholder="email address"/>
                    <input type="password"  ref={passwordRef} placeholder="password"/>
                    <button>create</button>
                    <p className={classes.message}>Already registered? <Link to='/login'>LogIn</Link></p>
                </form>
            </div>
        </div>
    )
}

export default Signin;
