import { useDispatch, useSelector} from 'react-redux';
import Modal from '../../Pages/Modal';
import CartItem from './CartItem';
import classes from './Cart.module.css';
import { uiActions } from '../../store/ui';
import Checkout from './Checkout';

const Cart = (props) => {


  const Items = useSelector(state => state.cart.Items);
  const totalAmount = useSelector(state => state.cart.totalAmount);
  const dispatch = useDispatch();

  const amount = `Rs. ${totalAmount.toFixed(2)}`;
  const hasItems = Items.length > 0;

  const cartItems = (
    <ul className={classes['cart-items']}>
      {Items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.quantity}
          price={item.price}
          id ={item.id}
          description= {item.description}
        />
      ))}
    </ul>
  );

  return (
    <Modal onClose={props.onClose}>
      {!placeOrder &&    
        <>  
          {cartItems}
          <div className={classes.total}>
            <div>Total Amount</div>
            <div>{amount}</div>
          </div>
          <div className={classes.actions}>
            <button className={classes['button--alt']} onClick={() => {dispatch(uiActions.inVisibleCart())}}>
              Close
            </button>
            {hasItems && <button onClick={()=>{setPlaceOrder(prev=>!prev)}} className={classes.button}>Order</button>}
          </div>
        </>
      }
      {placeOrder && <Checkout onConfirm={()=>{setPlaceOrder(prev=>!prev);dispatch(uiActions.inVisibleCart())}} onCancel={()=>{setPlaceOrder(prev=>!prev)}}/>}
    </Modal>
  );
};

export default Cart;
