import { createSlice } from "@reduxjs/toolkit";

const initialUIState = {showCart : false, showAdd: false };
const ui = createSlice({
    name : "ui",
    initialState : initialUIState,
    reducers : {
        showCartHandler(state){state.showCart = !state.showCart},
        showAddHandler(state){state.showAdd= !state.showAdd},
    }
})

export const uiActions = ui.actions;

export default ui;
