import { createSlice } from "@reduxjs/toolkit";

const initialUIState = {showCart : false, notificationStatus : '', notificationTitle : '',  notificationMessage: ''};
const ui = createSlice({
    name : "ui",
    initialState : initialUIState,
    reducers : {
        visibleCart(state){state.showCart = true},
        inVisibleCart(state){state.showCart = false},
        notification(state, action){
            state.notificationMessage = action.payload.message,
            state.notificationStatus = action.payload.stat
        }
    }
})



export const uiActions = ui.actions;

export default ui;
