    name: "cartItems",
    initialState: initialCartState,
    reducers:{

        addItemsToCart(state, action){
            const items = [...state.cartItems];
            items.push(action.payload);
            state.cartItems = items;
            state.totalAmount += action.payload.amount; 
        },

