import { configureStore, createSlice } from '@reduxjs/toolkit'; 

const  initialCartState ={Items: [], totalAmount : 0, totalQuantity : 0};

const cartItems = createSlice({
    name: "cartItems",
    initialState: initialCartState,
    reducers:{

        addItemsToCart(state, action){
            state.totalAmount += action.payload.item.price * action.payload.quantity;
            state.totalQuantity += action.payload.quantity;
            const temp = action.payload.item;
            temp.quantity = 0;
            const item = state.Items.find(item => item.id===temp.id);
            if(item===undefined){
                temp.quantity += action.payload.quantity;
                console.log(temp);
                state.Items.push(temp);
            }
            else {
                item.quantity += action.payload.quantity;
                item.totalAmount += action.payload.item.price * action.payload.quantity;
            }
        },

        removeItemsFromCart(state, action){
            state.totalAmount -= action.payload.item.price * action.payload.quantity;
            state.totalQuantity -= action.payload.quantity;
            const item = state.Items.find( item => item.id===action.payload.item.id );
            if(item.quantity===1){
                const temp = state.Items.filter( item => item.id!==action.payload.item.id);
                state.Items = temp;
            }
            else {
                item.quantity -= action.payload.quantity;;
                item.totalAmount -= action.payload.item.price * action.payload.quantity;
            }
            
        }
    }
})

export const cartItemsActions = cartItems.actions;

const store = configureStore({
    reducer: { cart: cartItems.reducer}
})

export default store;
