import classes from './CartItem.module.css';
import {useDispatch} from 'react-redux';

const CartItem = (props) => {
  const price = `Rs. ${props.price.toFixed(2)}`;
  const dispatch = useDispatch(); 

  const removeItemHandler = () => {
    dispatch.cart.removeItemFromCart({
      item : {name : props.name, price : props.price, id: props.id, description: props.description,}, 
      quantity: 1
    });
  }

  const addItemHandler = () => {
    dispatch.addItemToCart({
      item : {name : props.name, price : props.price, id: props.id, description: props.description,}, 
      quantity: 1
    });
  }

  return (
    <li className={classes['cart-item']}>
      <div>
        <h2>{props.name}</h2>
        <div className={classes.summary}>
          <div className={classes.price}>{price}</div>
          <div className={classes.amount}>x {props.amount}</div>
        </div>
      </div>
      <div className={classes.actions}>
        <button onClick={removeItemHandler}>âˆ’</button>
        <button onClick={addItemHandler}>+</button>
      </div>
    </li>
  );
};

export default CartItem;
