import { createSlice } from "@reduxjs/toolkit";

const initialUIState = {showCart : false, showAdd: false, };
const ui = createSlice({
    name : "ui",
    initialState : initialUIState,
    reducers : {
        showCartHandler(state){!state.showCart},
        showA(state){state.showCart = false},
    }
})

export const uiActions = ui.actions;

export default ui;
