import { createSlice } from "@reduxjs/toolkit";

const initialUIState = {showCart : false, notification : null};
const ui = createSlice({
    name : "ui",
    initialState : initialUIState,
    reducers : {
        visibleCart(state){state.showCart = true},
        inVisibleCart(state){state.showCart = false},
        showNotification(state, action){
            state.notificationMessage = action.payload.message
            state.notificationStatus = action.payload.status
            state.notificationTitle = action.payload.title
        }
    }
})

export const uiActions = ui.actions;

export default ui;
