import { uiActions } from "./ui";
import { cartItemsActions } from "./cart";
export const getMealCart = () => {
    return async(dispatch) => {

        const getData = async () => {
            const response = await fetch('https://food-app-74f07-default-rtdb.firebaseio.com/cart.json');
            if(!response.ok){
                throw new Error('could not get cart items');
                
            }

            const data = await response.json();
            // console.log(data);
            return data;
        }

        try {
            let data = await getData();
            if(data.items===null){
                data = {
                    items : [],
                    totalAmount : 0,
                    totalQuantity : 0
                    
                }
            }
            dispatch(cartItemsActions.replace({
                items : data.items || [], 
                totalAmount: data.totalAmount, 
                totalQuantity: data.totalQuantity,
                changed : data.changed
            }));

        } catch (error) {
            console.log(error.message);
            dispatch(uiActions.notification({
                title: "error",
                status: "error",
                message: "cannot access cart"
            }))
        }
    }
}



export const putMealCart = (cart) => {
    // console.log(cart);
    // const items = cart.items===undefined ? [] : cart.items
    return async(dispatch) => {
        
        const putData = async() => {
            const response = await fetch('https://food-app-74f07-default-rtdb.firebaseio.com/cart.json',{
                method : "PUT",
                body : JSON.stringify(
                    cart
                //     {
                //     items: cart.items, 
                //     totalAmount: cart.totalAmount, 
                //     totalQuantity: cart.totalQuantity
                // }
                )
            });

            if(!response.ok){
                throw new Error('could not upload to the cart')
            }
        }

        try {
            await putData();
            dispatch(uiActions.notification({
                title: "success",
                status: "success",
                message: "successfully updated cart"
            }))
        } catch (error) {
            console.log(error.message)
            dispatch(uiActions.notification({
                title: "error",
                status: "error",
                message: "cannot update cart"
            }))
        }

    }
}
