import { configureStore, createSlice } from '@reduxjs/toolkit'; 

const  initialCartState ={Items: [], totalAmount : 0, totalQuantity : 0};

const cartItems = createSlice({
    name: "cartItems",
    initialState: initialCartState,
    reducers:{

        addItemsToCart(state, action){
            state.totalAmount += item.amount*action.payload.amount;
            state.totalQuantity += action.payload.amount;
            const temp = action.payload.item;
            const item = state.Items.filter( item => item.id===temp.id);
            if(item==={}){
                temp.quantity = 0;
                temp.totalAmount = 0;
                item=temp;
            }
            item.quantity++;
            item.totalAmount += item.amount
            
        },

        removeItemsToCart(state, action){
            state.totalAmount -= action.payload.item.amount*action.payload.quantity;
            state.totalQuantity--;
            const item = state.Items.filter( item => item.id===action.payload.id );
            if(item.quantity===1){
                const temp = state.Items.filter( item => item.id!==action.payload.id);
                state.Items = temp;
            }
            else {
                item.quantity--;
                item.totalAmount -= item.amount;
            }
            
        }
    }
})

export const artItemsAction = cartItems.actions;

const store = configureStore({
    reducer: { meal: cartItems.reducer}
})

export default store;
